// Template (component)
function CourseCard({ title, description }) {
  return (
    <div className="card">
      <h2>{title}</h2>
      <p>{description}</p>
      <button>Enroll</button>
    </div>
  );
}

// Dynamic rendering (using the component)
const courses = [
  { title: "React Basics", description: "Learn React from scratch." },
  { title: "Advanced JS", description: "Deep dive into JavaScript." }
];

function CourseList() {
  return (
    <div>
      {courses.map((course, index) => (
        <CourseCard 
          key={index} 
          title={course.title} 
          description={course.description} 
        />
      ))}
    </div>
  );
}
